// ------------------ ┌∩┐(◣_◢)┌∩┐ ------------------
import request from 'request';

import React, { Component } from 'react';
import { Jumbotron } from 'reactstrap';

import "../basics.css";

import Footer from './Footer';
import BlockDataSpace from './BlockDataSpace';

//REDUX-o

// ------------------ ┌∩┐(◣_◢)┌∩┐ ------------------
class Explorer extends Component{
  constructor(props){
    super(props);

    this.state={
      bit_input:"",
      data: {},
      searched: false
    }

    this.handleSearch=this.handleSearch.bind(this);

  };

// ------------------ '(◣_◢)' ------------------
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
}
// ------------------ '(◣_◢)' ------------------
  handleSearch(){
    //create a regex identifier if bit_input is blockHeight, blockHash, TxHash or Address  
    const isHeight = /^(0|[1-9][0-9]*)$/;
    const isAddress = /^[2][a-km-zA-HJ-NP-Z0-9]{34}$/;
    const isHash = /^[a-fA-F0-9]{64}$/;
    if(isHeight.test(this.state.bit_input)){
      this.getBlock(this.state.bit_input)
      .then(result=>{
        console.log(result);
        this.setState({data: result.message});
      });

    }
    else if (isAddress.test(this.state.bit_input)){
      console.log("Bit input is Bitcoin Testnet Address")
    }
    else if (isHash.test(this.state.bit_input)){
      console.log("Bit input is Bitcoin Testnet Block or Tx Hash")
    }
    else{
      alert("Inputs not understood!\nEither enter:\nBlock #: Integer\nBitcoin Testnet Address: 35-bit string starting with \'2\'\nBlock or TxHash: 64-bit string ");
      console.log("Invalid inputs");    }
  }

// ------------------ '(◣_◢)' ------------------

  getBlock(height){
    return new Promise((resolve, reject)=>{

      const options = {
        method: 'POST',
        url:'http://127.0.0.1:9090/get_block',
        headers:
        {
        "Content-Type": 'application/json'
        },
        body: {
            'height': height, 
        },
        json: true
      };

      request(options, (error, response, body) => {
          console.log("Successfully sent request!");
          if(error){
              console.error(new Error(error));
              reject(error);
          }

          resolve(body);
      });


    });
  }

// ------------------ '(◣_◢)' ------------------

// ------------------ '(◣_◢)' ------------------
  render(){
    return(
      <div className="white-back">

          <Jumbotron className="blue-back" fluid>
            <h2 className="yellow-font text_center">Bitcoin Testnet Explorer</h2>
            <div className="col-lg-6 col_centered">
              <div className="row">
                <input  name='bit_input' type="bitdata" style={{background:"transparent"}} class="form-control form-control-lg text_center" id="bitdata" placeholder="Enter a block hash, block height, transaction hash or address." onChange={event => this.handleChange(event)} />
              </div>
              <br />
              <div className="row">
              <div className="col-lg-6 col_centered" style={{textAlign: "center",display: "flex", alignItems: "center",justifyContent:"center"}}>
                <button className="btn btn-lg outline-primary" type="submit" onClick={this.handleSearch}>
                  Search
                </button>
              </div>
              </div>
            </div>


          </Jumbotron>

          <div className="block_data container-fluid row black-font">
            <div className="col text-center">
              <h1>Data</h1>
              <hr />
              <div className="row text-center">

                  <BlockDataSpace data={this.state.data} />

              </div>
            </div>
          </div>

        <Footer />

      </div>

    );
  }
};
// ------------------° ̿ ̿'''\̵͇̿̿\з=(◕_◕)=ε/̵͇̿̿/'̿'̿ ̿ °------------------

// ------------------° ̿ ̿'''\̵͇̿̿\з=(◕_◕)=ε/̵͇̿̿/'̿'̿ ̿ °------------------
export default Explorer;
