// ------------------ ┌∩┐(◣_◢)┌∩┐ ------------------
import React, {Component} from 'react';
import "../basics.css";
const koineximg = "https://res.cloudinary.com/coinhero-in/image/fetch/https://pbs.twimg.com/profile_images/893388168795271168/-aT7tvnI_400x400.jpg"
const geminiimg = "https://coin.market/img/exchanges/gemini.svg"
// ------------------ ┌∩┐(◣_◢)┌∩┐ ------------------
class BTCConvert extends Component{
  constructor(props){
    super(props);

    this.state = {
      koinex: 0.0000,
      gemini: 0.0000,
      inr:0.0000,
      btc:0.0000,
      usd:0.0000
    };
    this.convertBTCToINR = this.convertBTCToINR.bind(this);
    this.convertBTCToUSD = this.convertBTCToUSD.bind(this);
  }

  componentDidMount(){
    this.getPriceAtGemini();
    this.getPriceAtKoinex();
  }

  handleChange (event) {
    this.setState({[event.target.name]: parseFloat(event.target.value)});
    this.convertBTCToINR(parseFloat(event.target.value));
    this.convertBTCToUSD(parseFloat(event.target.value));
  }

  convertBTCToINR(btc_amount){
    const inr_value = this.state.koinex * btc_amount;
    this.setState({inr: Math.round(inr_value * 100) / 100});
  }
  convertBTCToUSD(btc_amount){
    const usd_value =  this.state.gemini *btc_amount;
    this.setState({usd: Math.round(usd_value * 100) / 100});
  }
  getPriceAtKoinex(){
    fetch('https://koinex.in/api/ticker/',{
      crossDomain:true,
      method: 'GET',
      headers: {'Content-Type':'application/json'}
      })
      .then(response => response.json())
      .then(koinex => {
        this.setState({ koinex: parseFloat(koinex.prices.inr.BTC) })
      });

  }

  addCommas(nStr){
  	nStr += '';
  	let x = nStr.split('.');
  	let x1 = x[0];
  	let x2 = x.length > 1 ? '.' + x[1] : '';
  	const rgx = /(\d+)(\d{3})/;
  	while (rgx.test(x1)) {
  		x1 = x1.replace(rgx, '$1' + ',' + '$2');
  	}
  	return x1 + x2;
  }

  getPriceAtGemini(){
      fetch('https://api.gemini.com/v1/pubticker/btcusd',{
        crossDomain:true,
        method: 'GET',
        headers: {'Content-Type':'application/json'}
        })
        .then(response => response.json())
        .then(gemini => {
          this.setState({gemini: parseFloat(gemini.ask)})

        })
  }
// ------------------ '(◣_◢)' ------------------
//Centering elements
//The key is to set an offset equal to half of the remaining size of the row.
//So for example, a column of size 2 would be centered by adding an offset of 5, that's (12-2)/2.
//  <div class="col-md-2 col-md-offset-5"></div>
// Above is the same as the more efficient version Used
// Check basics.css to find the custom class col_centered which uses margin 0 auto and no float to override bootstraps thingy

  render(){
    console.log(this.state.inr);
    return(

      <div>
      <div className="container-fluid text_center">
        <br />
        <h1> BitConvert </h1>
        <br />
  <hr />
        <div className="row">
          <div className="col">
          <img src={koineximg} style={{borderRadius: "100%", width: "105px", height: "105px"}}/>
          <br />
          <br />
          <br />
          <p> 1 BTC = {this.state.koinex} INR </p>
          </div>
          <div className="col">
          <img src={geminiimg} style={{borderRadius: "100%", width: "130px", height: "130px" ,position: "relative",top: "-10px"}}/>
          <br />
          <br />
          <p> 1 BTC = {this.state.gemini} USD </p>
          </div>
        </div>
  <hr />
        <form>
        <div class="col-lg-2 col_centered" >
            <input name='btc' type="integer" style={{background:"transparent", color:"#f7f7f2"}} className="white-font form-control form-control-lg text_center" id="exampleAmount" placeholder="0.0000 BTC" onChange={event => this.handleChange(event)} />
        </div>
        </form>
  <hr />

        <div className="row">
          <div className="col col_centered">
          <p className="text_center" style={{fontSize:"28px"}}> {this.addCommas(this.state.inr)} INR </p>
          </div>
          <div className="col col_centered">
          <p className="text_center" style={{fontSize:"28px"}}> {this.addCommas(this.state.usd)} USD </p>
          </div>
        </div>
  <hr />
      </div>
      </div>
    );
  }
// ------------------ '(◣_◢)' ------------------
};
// ------------------° ̿ ̿'''\̵͇̿̿\з=(◕_◕)=ε/̵͇̿̿/'̿'̿ ̿ °------------------

// ------------------° ̿ ̿'''\̵͇̿̿\з=(◕_◕)=ε/̵͇̿̿/'̿'̿ ̿ °------------------
export default BTCConvert;
//    ‛¯¯٭٭¯¯(▫▫)¯¯٭٭¯¯’      FIN     ‛¯¯٭٭¯¯(▫▫)¯¯٭٭¯¯’
