// ------------------ ┌∩┐(◣_◢)┌∩┐ ------------------
import request from 'request';

import React, { Component } from 'react';
import { Jumbotron } from 'reactstrap';

import "../basics.css";

import Footer from './Footer';
import BlockDataSpace from './BlockDataSpace';
import TxDataSpace from './TxDataSpace';

//REDUX-o

var node_interface = `http://${process.env.NI_IP}:9090`;
// ------------------ ┌∩┐(◣_◢)┌∩┐ ------------------
class Explorer extends Component{
  constructor(props){
    super(props);
    console.log(node_interface);
    this.state={
      bit_input:"",
      data: {},
      searched: false,
      searchedBlock: false,
      searchedTx: false,
      searchedAddr: false,
      blockHeight: 0
    }
    console.log(`node ip = ${node_interface}`);
    this.handleSearch=this.handleSearch.bind(this);

  };

// ------------------ '(◣_◢)' ------------------
  handleChange(event){
    this.setState({[event.target.name]: event.target.value});
}
// ------------------ '(◣_◢)' ------------------
  handleSearch(){
    //create a regex identifier if bit_input is blockHeight, blockHash, TxHash or Address  
    const isHeight = /^(0|[1-9][0-9]*)$/;
    const isAddress = /^[2][a-km-zA-HJ-NP-Z0-9]{34}$/;
    const isHash = /^[a-fA-F0-9]{64}$/;
    //--------------------------------------------
    if(isHeight.test(this.state.bit_input)){
      this.setState({searchedBlock: true, searchedTx: false, searchedAddr: false});
      this.getBlock(this.state.bit_input)
      .then(result=>{
        console.log(result);
        this.setState({data: result.message});
      });

    }
    //--------------------------------------------
    else if (isAddress.test(this.state.bit_input)){
      this.setState({searchedBlock: false, searchedTx: false, searchedAddr: true});
      console.log("Bit input is Bitcoin Testnet Address")
    }
    //--------------------------------------------
    else if (isHash.test(this.state.bit_input)){
      this.setState({searchedBlock: false, searchedTx: true, searchedAddr: false});
      this.getTx(this.state.bit_input)
      .then(result=>{
        console.log(result);
        this.setState({data: result.message});
        this.getBlockFromHash(result.message.blockhash)
        .then(result=>{
          this.setState({blockHeight: result.message.height});
        })


      });
      console.log("Bit input is Bitcoin Testnet Block or Tx Hash")
    }
    //--------------------------------------------
    else{
      alert("Inputs not understood!\nEither enter:\nBlock #: Integer\nOR\nTxHash: 64-bit string\n");
      console.log("Invalid inputs");    }
  }
  //--------------------------------------------
// ------------------ '(◣_◢)' ------------------

  getBlock(height){
    return new Promise((resolve, reject)=>{
      console.log(`PRE REQUEST: ${node_interface}`);

      const options = {
        method: 'POST',
        url: `${node_interface}/get_block`,
        headers:
        {
        "Content-Type": 'application/json'
        },
        body: {
            'height': height, 
        },
        json: true
      };
      request(options, (error, response, body) => {
          console.log("Successfully sent request!");
          if(error){
              console.error(new Error(error));
              reject(error);
          }

          resolve(body);
      });
    });
  }
// ------------------ '(◣_◢)' ------------------

getBlockFromHash(hash){
  return new Promise((resolve, reject)=>{
    console.log(`PRE REQUEST BLOCKFROMHASH: ${node_interface}`);

    const options = {
      method: 'POST',
      url: `${node_interface}/get_block_from_hash`,
      headers:
      {
      "Content-Type": 'application/json'
      },
      body: {
          'hash': hash, 
      },
      json: true
    }
    request(options, (error, response, body) => {
        console.log("Successfully sent request!");
        if(error){
            console.error(new Error(error));
            reject(error);
        }

        resolve(body);
    });
  });
}
// ------------------ '(◣_◢)' ------------------
  getTx(txid){
    return new Promise((resolve, reject)=>{
      console.log(`PRE REQUEST: ${node_interface}`);

      const options = {
        method: 'POST',
        url: `${node_interface}/tx_detail_global`,
        headers:
        {
        "Content-Type": 'application/json'
        },
        body: {
            'txid': txid, 
        },
        json: true
      };
      request(options, (error, response, body) => {
          console.log("Successfully sent request!");
          if(error){
              console.error(new Error(error));
              reject(error);
          }

          resolve(body);
      });
    });
  }

// ------------------ '(◣_◢)' ------------------
  render(){
    return(
      <div>
      <div className="cyano-back">

          <Jumbotron className="gun-back" fluid>
            <h2 className="orange-font text_center">Bitcoin Testnet Explorer</h2>
            <div className="col-lg-6 col_centered">
              <div className="row">
                <input  name='bit_input' type="bitdata" style={{background:"transparent", color:"#f7f7f2"}} className="form-control form-control-lg text_center" id="bitdata" placeholder="Enter block height or transaction hash." onChange={event => this.handleChange(event)} />
              </div>
              <br />
              <div className="row">
              <div className="col-lg-6 col_centered" style={{textAlign: "center",display: "flex", alignItems: "center",justifyContent:"center"}}>
                <button className="btn btn-lg outline-primary" type="submit" onClick={this.handleSearch}>
                  Search
                </button>
              </div>
              </div>
            </div>


          </Jumbotron>

          <div className="block_data container-fluid row white-font">
            <div className="col text-center">
              <p style={{fontSize: "17px"}}>data</p>
              <hr />
              <div className="row text-center">
                  {
                    this.state.searchedBlock ? (
                      <BlockDataSpace data={this.state.data} />
                    ) : (
                      <div></div>
                    )
                  }
                  {
                    this.state.searchedTx ? (
                      <TxDataSpace data={this.state.data} blockHeight={this.state.blockHeight} />
                    ) : (
                      <div></div>
                    )
                  }     
              </div>
            </div>
          </div>



      </div>
      <Footer />
      </div>

    );
  }
};
// ------------------° ̿ ̿'''\̵͇̿̿\з=(◕_◕)=ε/̵͇̿̿/'̿'̿ ̿ °------------------

// ------------------° ̿ ̿'''\̵͇̿̿\з=(◕_◕)=ε/̵͇̿̿/'̿'̿ ̿ °------------------
export default Explorer;
